mapper.update:

update fmea_fail_analysis
set analysis_standard = #{analysisStandard,jdbcType=INTEGER},
  fmea_fail_analysis_desc = #{fmeaFailAnalysisDesc,jdbcType=VARCHAR},
  function_id = #{functionId,jdbcType=INTEGER},
  severity = #{severity,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:

select id, analysis_standard, fmea_fail_analysis_desc, function_id, severity
from fmea_fail_analysis
where id = #{id,jdbcType=INTEGER}
