mapper.update:

update fmea_function
set function_standard = #{functionStandard,jdbcType=INTEGER},
  function_require = #{functionRequire,jdbcType=VARCHAR},
  fmea_structure_id = #{fmeaStructureId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:

select id, function_standard, function_require, fmea_structure_id
from fmea_function
where id = #{id,jdbcType=INTEGER}
